mapper.select:
		SELECT stadiumSerial serial,stadiumName,reservationId,price,reservedDate,reservationMonth,reservationDay,reservationTime from(
	    SELECT rownum rno,r.stadiumSerial,r.stadiumName,r.reservationId,r.price,r.reservedDate,r.reservationMonth,r.reservationDay,r.reservationTime From 
        (SELECT stadiumSerial,stadiumName,reservationId,price,to_char(reservedDate,'yyyy-mm-dd hh24:mi') reservedDate,reservationMonth,reservationDay,reservationTime FROM RESERVATIONRECORD 

	    where RESERVATIONID  = #{reservationId}
	    ORDER BY RESERVEDDATE desc)r
         )WHERE rno BETWEEN #{startNo} AND #{endNo}
